package homework_day03;

public class CheckCondition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int a = 55;
	        int b = 70;

	        // Check if at least one of the conditions a < 50 or a < b is true
	        if (a < 50 || a < b) {
	            System.out.println("At least one of the conditions is true.");
	        } else {
	            System.out.println("Both conditions are not true.");
	        }
	    }
	}
